import React, { Component } from 'react'
import { Menu, Segment, Image } from 'semantic-ui-react'

export default class MenuExampleSecondaryPointing extends Component {
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <div>
        <Menu pointing secondary>
          <Menu.Item
            name='bio'
            active={activeItem === 'bio'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='relationships'
            active={activeItem === 'relationships'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='friends'
            active={activeItem === 'friends'}
            onClick={this.handleItemClick}
          />
        </Menu>

          if (this.state.activeItem === "bio") {
            <Segment>
            <Image src='https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png' size='medium' circular />
          </Segment>
          } else if (this.state.activeItem === "relationships") {
            <Image src='' size='medium' circular />

          }
      </div>
    )
  }
}
