const userReducer = ( state = { users: [], requesting: false}, action) => {
  switch(action.type) {
    case 'FETCH_USERS':
      return {
        ...state,
        users: [...state, users],
        requesting: true
      }

    case 'FETCH_USERS':
    return {
      ...state,
      users: action.users,
      requesting: false
    }


    default:
      return state;
  }
};

export default userReducer
