import React, { Component } from "react";
import { addUser } from "../store/actions/addUser";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Card } from "semantic-ui-react";
import { Button, Checkbox, Form } from 'semantic-ui-react'

class SignUp extends Component {
  state = {
    email: "",
    displayName: "",
    age: "",
    gender: "",
    lookingFor: "0101",
    zipCode: "",
    bio: ""
  }

  handleChange = (e) => {
    const name = e.target.name
    this.setState({
      [name]: e.target.value
    })
    console.log(this.state)
  }

  handleSubmit = (e) => {
    e.preventDefault()
    this.props.addUser(this.state)
    this.setState({
      email: "",
      displayName: "",
      age: "",
      gender: "",
      lookingFor: "0101",
      zipCode: "",
      bio: ""
    })
  }

  render() {
    return (

      <Form to="/explore" onSubmit={this.handleSubmit} className="ui main">
        <Form.Field>
          <label>Email</label>
          <input name="email" type="text" value={this.state.email} onChange={this.handleChange}></input>
        </Form.Field>
        <Form.Field>
          <label>Display Name</label>
          <input name="displayName" type="text" value={this.state.displayName} onChange={this.handleChange}></input>
        </Form.Field>
        <Form.Field>
          <label>Age</label>
          <input name="age" type="number" value={this.state.age} onChange={this.handleChange}></input>
        </Form.Field>
        <Form.Field>
          <label>Gender</label>
          <input name="gender" type="text" value={this.state.gender} onChange={this.handleChange}></input>
        </Form.Field>
        <Form.Field>
          <label>Zip Code</label>
          <input name="zipCode" type="text" value={this.state.zipCode} onChange={this.handleChange}></input>
        </Form.Field>
        <Form.Field>
          <label>Bio</label>
          <input name="bio" type="text" value={this.state.bio} onChange={this.handleChange}></input>
        </Form.Field>
        <Button type='submit' className="olive">Submit</Button>
      </Form>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    addUser: formData => {
      dispatch(addUser(formData))
    }
  };
};

export default connect(null, mapDispatchToProps)(SignUp);
