import React, {Component} from 'react';
import { Card, Header, Icon } from 'semantic-ui-react'
import UserCard from './UserCard'
import ProfilePage from "./ProfilePage";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { fetchUsers } from "../store/actions/users";

class ExplorePage extends Component {
  state = {
    userShow: {}
  };

  componentDidMount = () => {
    this.props.getUsers();
  };

  userShow = user => {
    console.log(user);
    this.setState({ userShow: [user] });
  };

  render() {
    const { users } = this.props;
    console.log(users)

      let userCards = users.map(user=> {

      return <UserCard key={user.id} user={user} />})

    if (users && users.length > 0) {
      return (
        <Card.Group itemsPerRow={5} className="ui main">
          {userCards}
        </Card.Group>
      ) }

    else {
      return (
        <Header as='h2' icon textAlign='center'>
          <Icon name='users' circular />
          <Header.Content>Please Log In to start ruining everyone </Header.Content>
        </Header>
      )
    }
  }
}

const mapStateToProps = state => {
  return {
    users: state.users.map(user=> user.data.attributes)
  };
};

const mapDispatchToProps = dispatch => {
  return { getUsers: () => dispatch(fetchUsers()) };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ExplorePage);
