import React, { Component } from 'react'
import { Header, Image, Container, Button } from 'semantic-ui-react'
import { connect } from 'react-redux'

class ProfilePage extends Component {
  state = {
    activeItem: 'info',
    id: null
   }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  componentDidMount = () => {
    console.log(this.props)
    let id = this.props.match.params.user_id
    this.setState({id})
  }

  render() {
    const { activeItem } = this.state
    console.log(this.props.user)
    return (
      <div>
        <Container text>

        <Header as='h2'>{this.props.user.display_name}</Header>

        <Image align='right' src='https://react.semantic-ui.com/images/avatar/large/matthew.png' size='medium' circular />
        <Header as='h4'>Age: {this.props.age}</Header>
        <Header as='h4'>Gender: {this.props.gender}</Header>
        <Header as='h4'>Bio:</Header>
        <p>
          {this.props.bio}
        </p>
        <button className="ui button" onClick={(event)=> this.props.handleBackButton(event)}> Back</button>
        </Container>


      </div>

    )
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(ownProps)
  let id = ownProps.match.params.user_id
  console.log(id)
  return {
    user: state.users.find((user) => {
      return user.id === parseInt(id)
    })
  }
}

export default connect(mapStateToProps)(ProfilePage)
