import React, {Component} from 'react';
import { Card } from 'semantic-ui-react'
import UserCard from './UserCard'
import ProfilePage from './ProfilePage'
import { connect } from 'react-redux'
import { NavLink } from 'react-router-dom'
import { createUsers } from '../../store/actions/usersActions'


const USER_API = 'http://localhost:3001/users'

class ExplorePage extends Component {
  state = {
    userShow: {}
  }

  componentDidMount = () => {
    fetch(USER_API)
    .then(resp => resp.json())
    .then(users => console.log(users))
  }

  userShow = (user) => {
    console.log(user)
    this.setState({userShow: [user]})
  }

  handleBackButton = (event) => {
    console.log(event)
    this.setState({userShow: []})
  }

  render() {

    const { users } = this.props

    let userCards = users ? users.map(user=> <UserCard key={user.id} user={user} userShow={this.userShow}/>) : "no users"

    return (
      <div>
        {this.state.userShow.id ?
            <ProfilePage user={this.state.userShow} />
          : <Card.Group> {userCards} </Card.Group> }
      </div>
    )
  }

}

const mapStateToProps = (state) => {
  return {
    users: state.users
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExplorePage)
