const userReducer = (state = { all: [], requesting: false }, action) => {
  switch (action.type) {
    case "FETCH_USERS":
      return {
        all: action.users,
        requesting: true
      };
    case "FETCH_PROFILE":
      return {
        user: action.user,
        requesting: false
      };
    default:
      return state;
  }
};

export default userReducer;
