import React from 'react'
import { Grid, Item, Icon, Button } from "semantic-ui-react";
import { connect } from 'react-redux'
import { makeRelationship } from '../store/actions/relationships'



const RelationshipRequestItem = ({ relationship }) => {
  return(
        <Item>
            <Item.content floated left>
              <Link to={'/profile/' + relationship.user.id}>{relationship.user.display_name} LOVES YOU SOOOOO MUCH</Link>
            </Item.content>
        </Item>
      )
}

const mapDispatchToProps = dispatch => {
  return { requestRelationship: (id) => dispatch(makeRelationship(id)) };
};


export default connect(null, mapDispatchToProps)(RelationshipRequestItem);
