const userReducer = (state = { users: [], requesting: false }, action) => {
  switch (action.type) {
    case "FETCH_USERS":
      return {
        users: action.users,
        requesting: true
      };
    case "ADD_USER":
    console.log({ users: state.users.concat(action.payload) });

    return { users: state.users.concat(action.payload) };

    default:
      return state;
  }
};

export default userReducer;
