import React from 'react'
import { Grid, Item, Icon, Button } from "semantic-ui-react";
import { connect } from 'react-redux'
import { makeRelationship } from '../store/actions/relationships'
import { Link } from 'react-router-dom'



const RelationshipRequestItem = ({ relationship }) => {
  return(
        <Item>
            <Item.Content className="floated left">
              <Link to={'/profile/' + relationship.user.id}>{relationship.user.display_name} LOVES YOU SOOOOO MUCH</Link>
            </Item.Content>
            <Item.Content>
              <Button onClick={() => {props.acceptRelationship(relationship.relationship_id)}}>Accept</Button>
              <Button>No Thanks</Button>
            </Item.Content>
        </Item>
      )
}

const mapDispatchToProps = dispatch => {
  return { acceptRelationship: (id) => dispatch(makeRelationship(id)) };
};


export default connect(null, mapDispatchToProps)(RelationshipRequestItem);
