import React, { Component } from "react";
import { connect } from "react-redux";
import RelationshipsList from "./RelationshipsList";
import { fetchCurrentUser, pickShowRelationship } from "../store/actions/users";
import MessagesShow from "./MessagesShow";
import { Grid } from "semantic-ui-react";

class MessagesPage extends Component {

  componentDidMount() {
    this.props.getCurrentUser();
  }

  selectRelationship = relationship => {
    this.props.setShowRelationship(relationship)
  };

  render() {
    return (
      <Grid className="ui main">
        <Grid.Column width={5}>
          <RelationshipsList
            selectRelationship={this.selectRelationship}
            activeRelationship={this.state.showRelationship}
          />
        </Grid.Column>
        <Grid.Column width={11}>
          {this.state.showRelationship ? (
            <MessagesShow
              currentUser={this.props.user}
              showRelationship={this.state.showRelationship}
              messages={this.state.showRelationship.messages}
              matchedUser={this.state.showRelationship.receiver == this.props.user ? this.state.showRelationship.requester : this.state.showRelationship.receiver}
            />
          ) : (
            <h2>No Relationship selected</h2>
          )}
        </Grid.Column>
      </Grid>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return { getCurrentUser: () => dispatch(fetchCurrentUser()),
  setShowRelationship: (relationship) => dispatch(pickShowRelationship(relationship)) };
};

const mapStateToProps = state => {
  return {
    relationships: state.User.relationships,
    user: state.User.current_user,
    showRelationship: state.User.showRelationship
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MessagesPage);
